// app.use('/uploads', express.static('./uploads'));

// app.use(cors());
// // Routes
// app.use("/api", userRoutes);
// app.use('/api/issues',issueRoutes);
// app.use('/api/attachments', attachmentRoutes);

// // server is running
// app.listen(port,() => {
//      console.log(`app is listening to port ${port}`);
