sourcesContent:
// import express from 'express';
// import multer from 'multer';
// import userController from '../controllers/userController.js';

// const router = express.Router();
// const upload = multer({ dest: 'uploads/' });

// // Create a user with image upload
// router.post('/register', upload.single('profileImage'), userController.createUser);
// router.post('/login', userController.loginUser);
// // router.patch('/userUpdate/:id', upload.single('profileImage'), userController.updateUserProfile);


// export default router;

import express from "express";
import {
  uploadProfilePicture,
  updateProfilePictureById,

} from "../controllers/userController";

import userController from '../controllers/userController'
const router = express.Router();

// User Routes
router.post('/register', userController.createUser); // Create a new user
router.get("/users/all", userController.getAllUsers); // Get all users
router.get("/users/:id", userController.getUserById); // Get a user by ID
router.put("/users/:id", userController.updateUserById); // Update a user by ID
router.delete("/users/:id", userController.deleteUserById); // Delete a user by ID

// User Profile Picture Upload Route
router.post("/users/:id/upload", uploadProfilePicture, updateProfilePictureById);

// User Login Route
router.post("/login", userController.loginUser);

export default router;

