    },
    createdAt: {
      type: Date,
      default: Date.now,
    },
    isRead: {
      type: Boolean,
      default: false,
    },
  });
  
  // Create the Notification Model
  const Notification = mongoose.model('Notification', notificationSchema);
  
  module.exports = Notification;

